-- database unit test for evo.add_video
DECLARE @RC AS INT, 
	@imdb_id AS VARCHAR (32), 
	@title AS VARCHAR (64), 
	@mpaa_rating AS VARCHAR (8), 
	@runtime AS DECIMAL (5, 2), 
	@plot AS VARCHAR (8000), 
	@video_type AS VARCHAR (8), 
	@release_date AS DATETIME, 
	@resolution AS VARCHAR (16), 
	@codec AS VARCHAR (8), 
	@modified_by AS VARCHAR (64), 
	@GENRES AS [evo].[GenreTableType], 
	@PERSONS AS [evo].[PersonTableType], 
	@RATINGS AS [evo].[RatingTableType];

SELECT @RC = 0,
       @imdb_id = 'tt1234',
       @title = 'Video Title goes here',
       @mpaa_rating = 'PG-13',
       @runtime = 120.95,
       @plot = 'some plot here',
       @video_type = 'movie',
       @release_date = getdate(),
       @resolution = '1080p',
       @codec = 'x265',
       @modified_by = 'ReturnsInsertedVideo';

INSERT INTO @GENRES(name) VALUES('Horror');
INSERT INTO @PERSONS(first_name, middle_name, last_name, suffix, role_name) 
	VALUES('John', 'F', 'Kennedy', 'III', 'Director');

INSERT INTO @RATINGS(source, value)
	VALUES ('Metacritic', 11.34);

EXECUTE @RC = [evo].[usp_add_movie_or_series] @imdb_id, @title, @mpaa_rating, @runtime, @plot, @video_type, @release_date, @resolution, @codec, null, @GENRES, @PERSONS, @RATINGS;

